export default {
  data: () => ({
    drawer: null,
    model: '',
    search: null,
    movies: []
  }),
  mounted() {
    this.loadMovies();
  },
  methods: {
    loadMovies: async function () {
      try {
        const response = await this.$http.get("/movie/popular");
        this.movies = response.data.results;
      } catch (error) {
        console.log(error);
      }
    }
  }
};
